package Looping;
import java.util.*;

public class SUMofn {

	public static void main(String[] args) {
		Scanner sc=new Scanner(System.in);
		int a=sc.nextInt();
		int sum=0;
		int i=0;
		for(;i<=a;i++) {
			sum+=i;
			//System.out.println(sum);
		}
		System.out.println(sum);
		// TODO Auto-generated method stub

	}

}
